<div id="header">
    <div id="header-bkg">
        <div id="header-inner">
                <div id="header-left">
                    <div id="header-icon">
                        <img src="/img/icons/ico_env.png" alt="">
                    </div>
                    <div id="header-title">
                        <h1>Environment Name</h1>
