<progress id="prog-bar" min=0 max=100 value=0></progress>

<section id="interface">

    <div id="model"></div>
    
    <div id="model-interaction">
        <p>Interact</p>
        <div id="default" class="btn btn--interact btn--selected"><img src="/img/icons/ico_touch.png" alt="Use Touch"></div>
        <div id="webcam" class="btn btn--interact"><img src="/img/icons/ico_webcam.png" alt="Use Webcam"></div>
    </div>
    <div id="model-controls">
            <input type="checkbox" id="menu-toggle"/>
            <label for="menu-toggle" id="menu-btn" class="btn--menu"></label>
        <div id="model-views">
            <p>Views</p>
            <div id="360" class="btn btn--views btn--selected">360</div>
            <div id="top" class="btn btn--views">TOP</div>
            <div id="side" class="btn btn--views">SIDE</div>
            <div id="live" class="btn btn--views btn--views--live">LIVE</div>
        </div>      
    </div>
        

</section>
